function batterySetup(){document.getElementById("batteryLevelEnabled").checked=userSettings.batteryLevelEnabled,document.getElementById("batteryLevelNumeric").checked=userSettings.batteryLevelNumeric,document.getElementById("batteryCapacityMax").value=userSettings.batteryCapacityMax}function wsBatteryStatus(e){var t=e.message,a=t.batteryVoltage,n=t.batteryCapacityRemainingPercentage,r=t.batteryCapacityRemaining,y=t.batteryTemperature;document.getElementById("batteryVoltage").value=a,document.getElementById("batteryCapacityRemaining").value=r,document.getElementById("batteryCapacityRemainingPercentage").value=n,document.getElementById("batteryTemperature").value=y}function toggleBatterySettings(e){for(var t=document.getElementsByClassName("batteryOnly"),a=0;a<t.length;a++)t[a].style.display=e?"flex":"none"}function setBatterySettings(){userSettings.batteryLevelEnabled=document.getElementById("batteryLevelEnabled").checked,userSettings.batteryLevelNumeric=document.getElementById("batteryLevelNumeric").checked,userSettings.batteryCapacityMax=parseFloat(document.getElementById("batteryCapacityMax").value),setRestartRequired(),updateUserSettings()}function setBatteryFull(){sendWebsocketCommand("setBatteryFull")}